package main;

import java.util.HashMap;

//Classe che gestisce gli attuatori, la logica e l'interfaccia con il controller
public class CentralinaSerra {
	
	private StatoLuce[] luci;
	private HashMap<Ventilatore, StatoVentilatore> ventilatori;
	private int[] irrigatori;
	
	private Modalita modalita;
	private SerraSmartController controller;
	
	//Costruttore
	public CentralinaSerra(int n_luci, int n_irrigatori, SerraSmartController controller, Modalita modalita) {
		
		this.luci = new StatoLuce[n_luci];
		for(int i=0; i<n_luci;i++) {
			luci[i]=StatoLuce.OFF; //Inizialmente tutte le luci all'interno della serra sono spente
		}
		
		this.irrigatori = new int[n_irrigatori];
		for(int i=0; i<n_irrigatori; i++) {
			irrigatori[i]=0; //Inizialmente tutti gli irrigatori sono spenti
		}
		
		this.ventilatori = new HashMap<Ventilatore, StatoVentilatore>();
		this.ventilatori.put(Ventilatore.PRINCIPALE, StatoVentilatore.SPENTO); //Inizialmente entrambi i ventilatori sono spenti
		this.ventilatori.put(Ventilatore.SECONDARIO, StatoVentilatore.SPENTO);
		
		this.modalita=modalita;
		this.controller=controller;
	}
	
	//Metodo che permette di definire la modalit  di gestione della serra
	public void setModalita(Modalita m) {
        this.modalita = m;
    }
	
    public Modalita getModalita() {
        return modalita;
    }
    
    //Metodo che va ad accendere tutte le luci della serra
    public void accendiLuci() {
    	for(int i=0; i<luci.length;i++) {
			luci[i]=StatoLuce.ON;
		}
    }
    
    //Metodo che va a spegnere tutte le luci della serra
    public void spegniLuci() {
    	for(int i=0; i<luci.length;i++) {
			luci[i]=StatoLuce.OFF; 
		}
    }
    
    //Metodo che va ad accendere tutti i ventilatori della serra
    public void accendiVentilatori() {
    	ventilatori.replace(Ventilatore.PRINCIPALE, StatoVentilatore.SPENTO, StatoVentilatore.ACCESO);
    	ventilatori.replace(Ventilatore.SECONDARIO, StatoVentilatore.SPENTO, StatoVentilatore.ACCESO);
    }
    
    //Metodo che va a spegnere tutti i ventilatori della serra
    public void spegniVentilatori() {
    	ventilatori.replace(Ventilatore.PRINCIPALE, StatoVentilatore.ACCESO, StatoVentilatore.SPENTO);
    	ventilatori.replace(Ventilatore.SECONDARIO, StatoVentilatore.ACCESO, StatoVentilatore.SPENTO);
    }
    
    //Metodo che va ad accendere al massimo tutti gli irrigatori della serra
    public void accendiIrrigatori() {
    	for(int i=0; i<irrigatori.length;i++) {
			irrigatori[i]=100;
		}
    }
    
    //Metodo che va a spegnere tutti gli irrigatori della serra
    public void spegniIrrigatori() {
    	for(int i=0; i<irrigatori.length;i++) {
			irrigatori[i]=0;
		}
    }
    
    //Metodo che va ad aggiornare i valori di temperatura, umidità  e luminosità  all'interno della serra 
    public void aggiornaSensori(int temperatura, int umidita, int lux) {
    	
    	if (modalita == Modalita.AUTOMATICA) {
    		//Se la luce   insufficiente si accendono tutte le luci
            if(controller.luceInsufficiente(lux)) {
            	accendiLuci();
            }
            //Se la luce   troppo alta si spengono tutte le luci
            if(controller.luceTroppoAlta(lux)) {
            	spegniLuci();
            }
            //Se la temperatura   troppo bassa allora si spengono tutti i ventilatori e si accendono tutte le luci
            if(controller.temperaturaTroppoBassa(temperatura)) {
            	spegniVentilatori();
            	accendiLuci();
            }
            //Se la temperatura   troppo alta allora si accendono tutti i ventilatori della serra
            if(controller.temperaturaTroppoAlta(temperatura)) {
            	accendiVentilatori();
            }
            //Se l'umidit    troppo bassa si accendono al massimo tutti gli irrigatori
            if(controller.umiditaTroppoBassa(umidita)) {
            	accendiIrrigatori();
            }
            //Se l'umidit    troppo alta si spengono tutti gli irrigatori
            if(controller.umiditaTroppoAlta(umidita)) {
            	spegniIrrigatori();
            }
            
        }

    }
    
    //Metodo che permette di accendere oppure spegnere una luce manualmente
    public void setLuce(int l, StatoLuce s) {
    	
    	if(modalita==Modalita.MANUALE) {
    		if(l>=0 && l<luci.length) {
    			luci[l]=s;
    		}
    	}
    	
    }
    
    //Metodo che permette di accendere oppure spegnere un ventilatore manualmente
    public void setVentilatore(Ventilatore v, StatoVentilatore s) {
    	
    	if(modalita==Modalita.MANUALE) {
    		ventilatori.replace(v, s);
    	}
    	
    }
    
    
    //Metodo che permette di settare il livello di apertura di un particolare irrigatore all'interno della serra
    public void setIrrigatore(int i, int livello) {
    	
    	if(modalita==Modalita.MANUALE) {
    		if(i>=0 && i<irrigatori.length && livello>=0 && livello<=100) {
    			irrigatori[i]=livello;
    		}
    	}
    	
    }
    
    //Metodo che restituisce lo stato di una particolare luce all'interno della serra
    public StatoLuce getStatoLuce(int l) {
    	
    	StatoLuce sl=null;
    	
    	if(l>=0 && l<luci.length) {
    		sl=luci[l];
    	}
    	
    	return sl;
    }
    
    //Metodo che restituisce lo stato di un particolare ventilatore all'interno della serra
    public StatoVentilatore getStatoVentilatore(Ventilatore v) {
    	return ventilatori.get(v);
    }
    
    //Metodo che restituisce il livello di un particolare irrigatore all'interno della serra
    public int getLivelloIrrigatore(int i) {
    	
    	int si=-1; //se il numero dell'irrigatore non   presente, viene restituito un valore negativo
    	
    	if(i>=0 && i<irrigatori.length) {
    		si=irrigatori[i];
    	}
    	
    	return si;
    }
	
}

package main;

//Enumerativo che rappresenta la possibile modalità di gestione della serra, manuale o automatica
public enum Modalita {
	AUTOMATICA,
	MANUALE
}

package main;
//Questa classe conterrà la logica centrale per gestire gli attuatori in base ai valori ambientali
public class SerraSmartController {
	
	private /*@ spec_public */ int sogliaLuceMin;
	private /*@ spec_public */ int sogliaLuceMax;
    private /*@ spec_public */ int sogliaTempMin;
    private /*@ spec_public */ int sogliaTempMax;
    private /*@ spec_public */ int sogliaUmiditaMin;
    private /*@ spec_public */ int sogliaUmiditaMax;
    
    //Definizione invarianti
    //1- Le soglie di luminosità massima e minima devono essere sempre >=0 e <=1000 (espressi in lux)
    //2- Le soglie di temperatura massima e minima devono essere sempre >=0 e <=100 (espressi in gradi centigradi)
    //3- Le soglie di umidità massima e minima devono essere sempre >=0 e <=100 (espressi in %)
    //4- La soglia minima deve essere sempre minore o uguale rispetto alla soglia massima
    //@ public invariant sogliaLuceMin>=0 && sogliaLuceMin<=1000;
    //@ public invariant sogliaLuceMax>=0 && sogliaLuceMax<=1000;
    //@ public invariant sogliaTempMin>=0 && sogliaTempMin<=100;
    //@ public invariant sogliaTempMax>=0 && sogliaTempMax<=100;
    //@ public invariant sogliaUmiditaMin>=0 && sogliaUmiditaMin<=100;
    //@ public invariant sogliaUmiditaMax>=0 && sogliaUmiditaMax<=100;
    //@ public invariant sogliaLuceMin<=sogliaLuceMax;
    //@ public invariant sogliaTempMin<=sogliaTempMax;
    //@ public invariant sogliaUmiditaMin<=sogliaUmiditaMax;
    
    //Costruttore
    //@ requires luceMin>=0 && luceMin<=1000 && luceMin<=luceMax;
    //@ requires luceMax>=0 && luceMax<=1000 && luceMax>=luceMin;
    //@ requires tempMin>=0 && tempMin<=100 && tempMin<=tempMax;
    //@ requires tempMax>=0 && tempMax<=100 && tempMax>=tempMin;
    //@ requires uMin>=0 && uMin<=100 && uMin<=uMax;
    //@ requires uMax>=0 && uMax<=100 && uMax>=uMin;
    //@ ensures sogliaLuceMin==luceMin;
    //@ ensures sogliaLuceMax==luceMax;
    //@ ensures sogliaTempMin==tempMin;
    //@ ensures sogliaTempMax==tempMax;
    //@ ensures sogliaUmiditaMin==uMin;
    //@ ensures sogliaUmiditaMax==uMax;
    public SerraSmartController(int luceMin, int luceMax, int tempMin, int tempMax, int uMin, int uMax) {
    	sogliaLuceMin=luceMin;
    	sogliaLuceMax=luceMax;
    	
    	sogliaTempMin=tempMin;
    	sogliaTempMax=tempMax;
    	
    	sogliaUmiditaMin=uMin;
    	sogliaUmiditaMax=uMax;
    	
    }
    
    //Metodo che specifica se la luminosità è insufficiente
    //Pre-condizioni:
    //1- La luminosità rilevata deve essere tra gli 0 e i 1000 lux
    //Post-condizioni:
    //1- il metodo deve restituire true se la luminosità è insufficiente, altrimenti false
    //@ requires lux >= 0 && lux <= 1000;
    //@ ensures \result <==> (lux < sogliaLuceMin);
    public /*@ pure @*/ boolean luceInsufficiente(int lux) {
        return lux < sogliaLuceMin;
    }
    
    //Metodo che specifica se la luminosità è troppo elevata
    //Pre-condizioni:
    //1- La luminosità rilevata deve essere tra gli 0 e i 1000 lux
    //Post-condizioni:
    //1- il metodo deve restituire true se la luminosità è troppo alta, altrimenti false
    //@ requires lux >= 0 && lux <= 1000;
    //@ ensures \result <==> (lux > sogliaLuceMax);
    public /*@ pure @*/ boolean luceTroppoAlta(int lux) {
        return lux > sogliaLuceMax;
    }
    
    //Metodo che specifica se la temperatura rilevata è troppo bassa
    //Pre-condizioni:
    //1- La temperatura rilevata deve essere al massimo 100 gradi centigradi
    //Post-condizioni:
    //1- il metodo deve restituire true se la temperatura è insufficiente, altrimenti false
    //@ requires temperatura <= 100;
    //@ ensures \result <==> (temperatura < sogliaTempMin);
    public /*@ pure @*/ boolean temperaturaTroppoBassa(int temperatura) {
        return temperatura < sogliaTempMin;
    }
    
    //Metodo che specifica se la temperatura rilevata è troppo alta
    //Pre-condizioni:
    //1- La temperatura rilevata deve essere al massimo 100 gradi centigradi
    //Post-condizioni:
    //1- il metodo deve restituire true se la temperatura è troppo elevata, altrimenti false
    //@ requires temperatura <= 100;
    //@ ensures \result <==> (temperatura > sogliaTempMax);
    public /*@ pure @*/ boolean temperaturaTroppoAlta(int temperatura) {
        return temperatura > sogliaTempMax;
    }

    
    //Metodo che specifica se l'umidità rilevata è troppo bassa
    //Pre-condizioni:
    //1- L'umidità rilevata deve essere compresa tra lo 0% e il 100%
    //Post-condizioni:
    //1- il metodo deve restituire true se l'umidità è insufficiente, altrimenti false
    //@ requires umidita >= 0 && umidita <= 100;
    //@ ensures \result <==> (umidita < sogliaUmiditaMin);
    public /*@ pure @*/ boolean umiditaTroppoBassa(int umidita) {
        return umidita < sogliaUmiditaMin;
    }
    
    //Metodo che specifica se l'umidità rilevata è troppo alta
    //Pre-condizioni:
    //1- L'umidità rilevata deve essere compresa tra lo 0% e il 100%
    //Post-condizioni:
    //1- il metodo deve restituire true se l'umidità è troppo alta, altrimenti false
    //@ requires umidita >= 0 && umidita <= 100;
    //@ ensures \result <==> (umidita > sogliaUmiditaMax);
    public /*@ pure @*/ boolean umiditaTroppoAlta(int umidita) {
        return umidita > sogliaUmiditaMax;
    }
    
    //Metodo per settare la luminosità minima
    //@ requires l >= 0 && l <= 1000 && l<=sogliaLuceMax;
    //@ ensures sogliaLuceMin == l;
    public void setSogliaLuceMin(int l) {
        sogliaLuceMin = l;
    }
    
    //Metodo per settare la luminosità massima
    //@ requires l >= 0 && l <= 1000 && l>=sogliaLuceMin;
    //@ ensures sogliaLuceMax == l;
    public void setSogliaLuceMax(int l) {
        sogliaLuceMax = l;
    }
    
    //Metodo per settare la temperatura minima
    //@ requires t >= 0 && t <= 100 && t<=sogliaTempMax;
    //@ ensures sogliaTempMin == t;
    public void setSogliaTempMin(int t) {
        sogliaTempMin = t;
    }
    
    //Metodo per settare la temperatura massima
    //@ requires t >= 0 && t <= 100 && t>=sogliaTempMin;
    //@ ensures sogliaTempMax == t;
    public void setSogliaTempMax(int t) {
        sogliaTempMax = t;
    }

    //Metodo per settare l'umidità minima
    //@ requires u >= 0 && u <= 100 && u<=sogliaUmiditaMax;
    //@ ensures sogliaUmiditaMin == u;
    public void setSogliaUmiditaMin(int u) {
        sogliaUmiditaMin = u;
    }
    
    //Metodo per settare l'umidità massima
    //@ requires u >= 0 && u <= 100 && u>=sogliaUmiditaMin;
    //@ ensures sogliaUmiditaMax == u;
    public void setSogliaUmiditaMax(int u) {
        sogliaUmiditaMax = u;
    }
}

package main;

//Enumerativo che rappresenta lo stato in cui può essere una luce
public enum StatoLuce {
	ON,
	OFF
}

package main;

//Enumerativo che rappresenta lo stato in cui può essere un ventilatore
public enum StatoVentilatore {
	ACCESO,
	SPENTO
}

package main;

//Enumerativo che specifica i 2 tipi possibili di ventilatore
public enum Ventilatore {
	PRINCIPALE,
	SECONDARIO
}

package test;

import static org.junit.jupiter.api.Assertions.*;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import main.CentralinaSerra;
import main.Modalita;
import main.SerraSmartController;
import main.StatoLuce;
import main.StatoVentilatore;
import main.Ventilatore;

//Classe di test per la classe CentralinaSerra
class CentralinaSerraTest {
	
    private CentralinaSerra centralina;

    @BeforeEach
    public void setUp() {
        // Soglie: luceMin=200, luceMax=800, tempMin=15, tempMax=30, umiditaMin=30, umiditaMax=70
        SerraSmartController controller = new SerraSmartController(200, 800, 15, 30, 30, 70);
        centralina = new CentralinaSerra(3, 2, controller, Modalita.AUTOMATICA);
    }

    @Test
    public void testLuceInsufficienteAccendeLuci() {
        centralina.aggiornaSensori(25, 50, 100); // luce < sogliaLuceMin
        // Tutte le luci devono essere accese
        for (int i = 0; i < 3; i++) {
            assertEquals(StatoLuce.ON, centralina.getStatoLuce(i));
        }
    }

    @Test
    public void testLuceTroppoAltaSpegneLuci() {
        centralina.accendiLuci(); // Prima accendiamo
        centralina.aggiornaSensori(25, 50, 900); // luce > sogliaLuceMax
        for (int i = 0; i < 3; i++) {
            assertEquals(StatoLuce.OFF, centralina.getStatoLuce(i));
        }
    }

    @Test
    public void testTemperaturaTroppoAltaAccendeVentilatori() {
        centralina.aggiornaSensori(40, 50, 300); // temperatura > sogliaTempMax
        assertEquals(StatoVentilatore.ACCESO, centralina.getStatoVentilatore(Ventilatore.PRINCIPALE));
        assertEquals(StatoVentilatore.ACCESO, centralina.getStatoVentilatore(Ventilatore.SECONDARIO));
    }

    @Test
    public void testTemperaturaTroppoBassaSpegneVentilatori() {
        centralina.accendiVentilatori();
        centralina.aggiornaSensori(10, 50, 300); // temperatura < sogliaTempMin
        assertEquals(StatoVentilatore.SPENTO, centralina.getStatoVentilatore(Ventilatore.PRINCIPALE));
    }

    @Test
    public void testUmiditaTroppoAltaSpegneIrrigatori() {
        centralina.accendiIrrigatori();
        centralina.aggiornaSensori(25, 90, 300); // umidit  > sogliaUmiditaMax
        for (int i = 0; i < 2; i++) {
            assertEquals(0, centralina.getLivelloIrrigatore(i));
        }
    }

    @Test
    public void testManualeModificaStato() {
        centralina.setModalita(Modalita.MANUALE);
        centralina.setLuce(0, StatoLuce.ON);
        assertEquals(StatoLuce.ON, centralina.getStatoLuce(0));
    }

}
